// === ACTIVATE BURGER MENU ===
const burgerMenu = () => {
  // $(".header__button-menu").on("click", function () {
  //   $(".menu").toggleClass("menu--opened");
  // });

  const burgerBtn = document.querySelector('.header__button-menu'),
    menu = document.querySelector('.menu');

  burgerBtn.addEventListener('click', () => menu.classList.toggle('menu--opened'));
}
burgerMenu();
// === / ACTIVATE BURGER MENU ===
// === NAV-MENU-ACTIVE-STYLE ===
const menuLinks = document.querySelectorAll('.menu__list-link'),
  url = window.location.pathname.slice(1, length - 5);

menuLinks.forEach(link => {
  link.classList.remove('menu__list-link--active');
  if (link.innerText.toLowerCase() === url) {
    link.classList.add('menu__list-link--active');
  }
});
// === / NAV-MENU-ACTIVE-STYLE ===